mapper.select:
select store_no, store_name, store_addr, store_introduce, store_time
, (select count(*) from favorite join favorite_folder using(favorite_folder_no) where store_no=#{storeNo} and user_no = #{userNo}) is_favorite
from store where store_no = #{storeNo}

select store_no, store_name, store_addr, store_phone, store_time,business_number,so_name,so_phone,map_x,map_y
from store 
where store_no = #{storeNo}

mapper.update:
update store set
so_pw =#{soPw},
regist_type = #{registType}, service_price = 1000, store_enroll_date = sysdate where store_no = #{storeNo}
